import React, { Component } from "react";
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";

import UserManagementService from "../services/user-management.service";

const required = value => {
  if (!value) {
    return (
      <div className="alert alert-danger" role="alert">
        This field is required!
      </div>
    );
  }
};

export default class UserManagement extends Component {
  constructor(props) {
    super(props);
    this.handleRequest = this.handleRequest.bind(this);
    this.onChangePassword = this.onChangePassword.bind(this);
    this.onChangePassword2 = this.onChangePassword2.bind(this);

    this.state = {
      username: "",
      password: "",
      password2: "",
      loading: false,
      message: ""
    };
  }


  onChangePassword(e) {
    this.setState({
      password: e.target.value
    });
  }

  onChangePassword2(e) {
    this.setState({
      password2: e.target.value
    });
  }

  handleRequest(e) {
    e.preventDefault();

    this.setState({
      message: "",
      loading: true
    });

    this.form.validateAll();

    if (this.checkBtn.context._errors.length === 0) {
      UserManagementService.UserManagement(this.state.username, this.state.password, this.state.password2).then(
        () => {
          this.props.history.push("/login");
          window.location.reload();
        },
        error => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();

          this.setState({
            loading: false,
            message: resMessage
          });
        }
      );
    } else {
      this.setState({
        loading: false
      });
    }
  }

  render() {
    return (
      <div id="app" className="container my-3">
        <h3>React Query Axios example</h3>

        <div className="card mt-3">
          <div className="card-header">React Query Axios GET - BezKoder.com</div>
          <div className="card-body">
            <div className="input-group input-group-sm">
              <button className="btn btn-sm btn-primary" onClick={getAllData}>
                Get All
              </button>

              <input
                type="text"
                value={getId}
                onChange={(e) => setGetId(e.target.value)}
                className="form-control ml-2"
                placeholder="Id"
              />
              <div className="input-group-append">
                <button className="btn btn-sm btn-primary" onClick={getDataById}>
                  Get by Id
                </button>
              </div>

              <input
                type="text"
                value={getTitle}
                onChange={(e) => setGetTitle(e.target.value)}
                className="form-control ml-2"
                placeholder="Title"
              />
              <div className="input-group-append">
                <button
                  className="btn btn-sm btn-primary"
                  onClick={getDataByTitle}
                >
                  Find By Title
                </button>
              </div>

              <button
                className="btn btn-sm btn-warning ml-2"
                onClick={clearGetOutput}
              >
                Clear
              </button>
            </div>

            {getResult && (
              <div className="alert alert-secondary mt-2" role="alert">
                <pre>{getResult}</pre>
              </div>
            )}
          </div>
        </div>

        <div className="card mt-3">
          <div className="card-header">React Query Axios POST - BezKoder.com</div>
          <div className="card-body">
            <div className="form-group">
              <input
                type="text"
                value={postTitle}
                onChange={(e) => setPostTitle(e.target.value)}
                className="form-control"
                placeholder="Title"
              />
            </div>
            <div className="form-group">
              <input
                type="text"
                value={postDescription}
                onChange={(e) => setPostDescription(e.target.value)}
                className="form-control"
                placeholder="Description"
              />
            </div>
            <button className="btn btn-sm btn-primary" onClick={postData}>
              Post Data
            </button>
            <button
              className="btn btn-sm btn-warning ml-2"
              onClick={clearPostOutput}
            >
              Clear
            </button>

            {postResult && (
              <div className="alert alert-secondary mt-2" role="alert">
                <pre>{postResult}</pre>
              </div>
            )}
          </div>
        </div>

        <div className="card mt-3">
          <div className="card-header">React Query Axios PUT - BezKoder.com</div>
          <div className="card-body">
            <div className="form-group">
              <input
                type="text"
                value={putId}
                onChange={(e) => setPutId(e.target.value)}
                className="form-control"
                placeholder="Id"
              />
            </div>
            <div className="form-group">
              <input
                type="text"
                value={putTitle}
                onChange={(e) => setPutTitle(e.target.value)}
                className="form-control"
                placeholder="Title"
              />
            </div>
            <div className="form-group">
              <input
                type="text"
                value={putDescription}
                onChange={(e) => setPutDescription(e.target.value)}
                className="form-control"
                placeholder="Description"
              />
            </div>
            <div className="form-check mb-2">
              <input
                type="checkbox"
                name="putPublished"
                checked={putPublished}
                onChange={(e) => setPutPublished(e.target.checked)}
                className="form-check-input"
              />
              <label className="form-check-label" htmlFor="putPublished">
                Publish
              </label>
            </div>
            <button className="btn btn-sm btn-primary" onClick={putData}>
              Update Data
            </button>
            <button
              className="btn btn-sm btn-warning ml-2"
              onClick={clearPutOutput}
            >
              Clear
            </button>

            {putResult && (
              <div className="alert alert-secondary mt-2" role="alert">
                <pre>{putResult}</pre>
              </div>
            )}
          </div>
        </div>

        <div className="card mt-3">
          <div className="card-header">
            React Query Axios DELETE - BezKoder.com
          </div>
          <div className="card-body">
            <div className="input-group input-group-sm">
              <button className="btn btn-sm btn-danger" onClick={deleteAllData}>
                Delete All
              </button>

              <input
                type="text"
                value={deleteId}
                onChange={(e) => setDeleteId(e.target.value)}
                className="form-control ml-2"
                placeholder="Id"
              />
              <div className="input-group-append">
                <button
                  className="btn btn-sm btn-danger"
                  onClick={deleteDataById}
                >
                  Delete by Id
                </button>
              </div>

              <button
                className="btn btn-sm btn-warning ml-2"
                onClick={clearDeleteOutput}
              >
                Clear
              </button>
            </div>

            {deleteResult && (
              <div className="alert alert-secondary mt-2" role="alert">
                <pre>{deleteResult}</pre>
              </div>
            )}
          </div>
        </div>
      </div>
    );
  }
}
