import React, { Component } from "react";
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import Select from "react-validation/build/select";
import CheckButton from "react-validation/build/button";
import { isEmail, isDate} from "validator";


import AuthService from "../services/auth.service";
import UserService from "../services/user.service";

const required = value => {
  if (!value) {
    return (
      <div className="alert alert-danger" role="alert">
        This field is required!
      </div>
    );
  }
};

const email = value => {
  if (!isEmail(value)) {
    return (
      <div className="alert alert-danger" role="alert">
        This is not a valid email.
      </div>
    );
  }
};

const date = value => {
  if (!isDate(value)) {
      return (
          <div className="alert alert-danger" role="alert">
              This is not a valid date.
          </div>
      );
  }
};

const vfirstName = value => {
  if (value.length < 2 || value.length > 40) {
    return (
      <div className="alert alert-danger" role="alert">
        The first name must be between 2 and 40 characters.
      </div>
    );
  }
};

const vlastName = value => {
  if (value.length < 2 || value.length > 40) {
    return (
      <div className="alert alert-danger" role="alert">
        The last name must be between 2 and 40 characters.
      </div>
    );
  }
};

const vaddress = value => {
  if (value.length < 2 || value.length > 40) {
    return (
      <div className="alert alert-danger" role="alert">
        The address must be between 2 and 40 characters.
      </div>
    );
  }
};

const vcity = value => {
  if (value.length < 2 || value.length > 40) {
    return (
      <div className="alert alert-danger" role="alert">
        The city must be between 2 and 40 characters.
      </div>
    );
  }
};

const vzip = value => {
  const zipletters =  /^([0-9]{5})$/;
  if (!zipletters.test(value) || value.toString().length != 5) {
    return (
      <div className="alert alert-danger" role="alert">
        The zip must all numbers and 5 characters.
      </div>
    );
  }
};

export default class ProfileEdit extends Component {
  constructor(props) {
    super(props);
    this.handleProfileEdit = this.handleProfileEdit.bind(this);
    this.onChangeEmail = this.onChangeEmail.bind(this);
    this.onChangeFirstName = this.onChangeFirstName.bind(this);
    this.onChangeLastName = this.onChangeLastName.bind(this);
    this.onChangeAddress = this.onChangeAddress.bind(this);
    this.onChangeGenderId = this.onChangeGenderId.bind(this);
    this.onChangePronounId = this.onChangePronounId.bind(this);
    this.onChangeSexAtBirthId = this.onChangeSexAtBirthId.bind(this);
    this.onChangeStateId = this.onChangeStateId.bind(this);
    this.onChangeBirthday = this.onChangeBirthday.bind(this);
    this.onChangeCity = this.onChangeCity.bind(this);
    this.onChangeZip = this.onChangeZip.bind(this);


    this.state = {
      ...AuthService.getCurrentUser(),
      successful: false,
      message: "",
      // dropdown options
      genders: [],
      pronouns: [],
      sexAtBirths: [],
      states: [],
    };

    this.state.birthday = new Date(this.state.birthday);
    this.state.birthday = this.state.birthday.toISOString().split('T')[0];

    console.log(this.state);
  }

  onChangeEmail(e) {
    this.setState({
      email: e.target.value
    });
  }

  onChangeFirstName(e){
    this.setState({
      firstName: e.target.value
    });
  }

  onChangeLastName(e){
    this.setState({
      lastName: e.target.value
    });
  }

  onChangeAddress(e){
    this.setState({
      address: e.target.value
    });
  }

  onChangeGenderId(e){
    this.setState({
      gender_id: e.target.value
    });
  }

  onChangePronounId(e){
    this.setState({
      pronoun_id: e.target.value
    });
  }

  onChangeSexAtBirthId(e){
    this.setState({
      sex_at_birth_id: e.target.value
    });
  }

  onChangeStateId(e){
    this.setState({
      state_id: e.target.value
    });
  }
  
  onChangeBirthday(e){
    this.setState({
      birthday: e.target.value
    });
  }

  onChangeCity(e){
    this.setState({
      city: e.target.value
    });
  }

  onChangeZip(e){
    this.setState({
      zip: e.target.value
    });
  }

  componentDidMount() {
    // get old user information


    // get drop down information
    UserService.getGenders().then(
      response => {
        this.setState({
          genders: response.data.result
        });
      },
      error => {
        this.setState({
          genders:
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString()
        });
      }
    );
    UserService.getPronouns().then(
      response => {
        this.setState({
          pronouns: response.data.result
        });
      },
      error => {
        this.setState({
          pronouns:
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString()
        });
      }
    );
    UserService.getSexAtBirths().then(
      response => {console.log(response.data.result);
        this.setState({
          sexAtBirths: response.data.result
        });
      },
      error => {
        this.setState({
          sexAtBirths:
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString()
        });
      }
    );
    UserService.getStates().then(
      response => {
        this.setState({
          states: response.data.result
        });
      },
      error => {
        this.setState({
          states:
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString()
        });
      }
    );
  }
  
  handleProfileEdit(e) {
    e.preventDefault();

    this.setState({
      message: "",
      loading: true
    });

    this.form.validateAll();

    if (this.checkBtn.context._errors.length === 0) {
      UserService.profileEdit(
        this.state.username,
        this.state.email,
        this.state.firstName,
        this.state.lastName,
        this.state.address,
        this.state.birthday,
        this.state.gender_id,
        this.state.pronoun_id,
        this.state.sex_at_birth_id,
        this.state.state_id,
        this.state.city,
        this.state.zip
      ).then(
        response => {
          this.setState({
            message: response.data.message,
            successful: true
          });
        },
        error => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();

          this.setState({
            successful: false,
            message: resMessage
          });
        }
      );
    }
  }

  render() {
    return (
      <div className="col-md-12">

          <Form
            onSubmit={this.handleProfileEdit}
            ref={c => {
              this.form = c;
            }}
          >
            {!this.state.successful && (
              <div>

                <div className="form-group">
                  <label htmlFor="email">Email</label>
                  <Input
                    type="text"
                    className="form-control"
                    name="email"
                    value={this.state.email}
                    onChange={this.onChangeEmail}
                    validations={[required, email]}
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="firstName">FirstName</label>
                  <Input
                    type="text"
                    className="form-control"
                    name="firstName"
                    value={this.state.firstName}
                    onChange={this.onChangeFirstName}
                    validations={[required, vfirstName]}
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="lastName">LastName</label>
                  <Input
                    type="text"
                    className="form-control"
                    name="lastName"
                    value={this.state.lastName}
                    onChange={this.onChangeLastName}
                    validations={[required, vlastName]}
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="address">Address</label>
                  <Input
                    type="text"
                    className="form-control"
                    name="address"
                    value={this.state.address}
                    onChange={this.onChangeAddress}
                    validations={[required, vaddress]}
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="birthday">Birthday</label>
                  <Input
                    type="text"
                    className="form-control"
                    name="birthday"
                    value={this.state.birthday}
                    onChange={this.onChangeBirthday}
                    validations={[required, date]}
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="gender_id">Gender</label>
                  <Select
                    value={this.state.gender_id}
                    onChange={this.onChangeGenderId}
                    validations={[required]}
                  >
                    <option value="">Select One</option>
                    {this.state.genders.length > 0 &&
                      this.state.genders.map(item => (
                        <option key={item.gender_id} value={item.gender_id}>
                          {item.gender}
                        </option>
                      ))}
                  </Select>
                </div>

                <div className="form-group">
                  <label htmlFor="pronoun_id">Pronoun</label>
                  <Select
                    value={this.state.pronoun_id}
                    onChange={this.onChangePronounId}
                    validations={[required]}
                  >
                    <option value="">Select One</option>
                    {this.state.pronouns.length > 0 &&
                      this.state.pronouns.map(item => (
                        <option key={item.pronoun_id} value={item.pronoun_id}>
                          {item.pronoun}
                        </option>
                      ))}
                  </Select>
                </div>
                
                <div className="form-group">
                  <label htmlFor="sex_at_birth_id">Sex At Birth</label>
                  <Select
                    value={this.state.sex_at_birth_id}
                    onChange={this.onChangeSexAtBirthId}
                    validations={[required]}
                  >
                    <option value="">Select One</option>
                    {this.state.sexAtBirths.length > 0 &&
                      this.state.sexAtBirths.map(item => (
                        <option key={item.sex_at_birth_id} value={item.sex_at_birth_id}>
                          {item.sex_at_birth}
                        </option>
                      ))}
                  </Select>
                </div>

                <div className="form-group">
                  <label htmlFor="state_id">State</label>
                  <Select
                    value={this.state.state_id}
                    onChange={this.onChangeStateId}
                    validations={[required]}
                  >
                    <option value="">Select One</option>
                    {this.state.states.length > 0 &&
                      this.state.states.map(item => (
                        <option key={item.state_id} value={item.state_id}>
                          {item.state}
                        </option>
                      ))}
                  </Select>
                </div>

                <div className="form-group">
                  <label htmlFor="city">City</label>
                  <Input
                    type="text"
                    className="form-control"
                    name="city"
                    value={this.state.city}
                    onChange={this.onChangeCity}
                    validations={[required, vcity]}
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="zip">Zip</label>
                  <Input
                    type="text"
                    className="form-control"
                    name="zip"
                    value={this.state.zip}
                    onChange={this.onChangeZip}
                    validations={[required, vzip]}
                  />
                </div>

                <div className="form-group">
                  <button className="btn btn-primary btn-block">Save</button>
                </div>
              </div>
            )}

            {this.state.message && (
              <div className="form-group">
                <div
                  className={
                    this.state.successful
                      ? "alert alert-success"
                      : "alert alert-danger"
                  }
                  role="alert"
                >
                  {this.state.message}
                </div>
              </div>
            )}
            <CheckButton
              style={{ display: "none" }}
              ref={c => {
                this.checkBtn = c;
              }}
            />
          </Form>
      </div>
    );
  }
}
