import React, { Component } from "react";
import { Switch, Route, Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

import AuthService from "./services/auth.service";

import Login from "./components/login.component";
import Register from "./components/register.component";

import Forgot from "./components/forgot.component";
import ForgotUserAccessCode from "./components/forgot-user-access-code.component";
import ResetPassword from "./components/reset-password.component";

import Home from "./components/home.component";

import Profile from "./components/profile.component";
import ProfileEdit from "./components/profile-edit.component";

import BoardUser from "./components/board-user.component";
//import BoardModerator from "./components/board-moderator.component";
import BoardAdmin from "./components/board-admin.component";

import PersonManagementCreate from "./components/person-management-create.component";
import PersonManagement from "./components/person-management.component";
import PersonManagementList from "./components/person-management-list";

// import AuthVerify from "./common/auth-verify";
import EventBus from "./common/EventBus";

class App extends Component {
  constructor(props) {
    super(props);
    this.logOut = this.logOut.bind(this);

    this.state = {
      //showModeratorBoard: false,
      showAdminBoard: false,
      currentUser: undefined,
    };
  }

  componentDidMount() {
    const user = AuthService.getCurrentUser();

    if (user) {
      this.setState({
        currentUser: user,
        //showModeratorBoard: user.roles.includes("ROLE_MODERATOR"),
        showAdminBoard: user.roles.includes("ROLE_ADMIN"),
      });
    }
    
    EventBus.on("logout", () => {
      EventBus.remove("logout");
    });
  }

  logOut() {
    AuthService.logout();
    this.setState({
      //showModeratorBoard: false,
      showAdminBoard: false,
      currentUser: undefined,
    });
  }

  render() {
    const { currentUser, /*showModeratorBoard,*/ showAdminBoard } = this.state;

    return (
      <div>
        <nav className="navbar navbar-expand navbar-dark bg-dark">
          <Link to={"/"} className="navbar-brand">
            Healthcare
          </Link>
          <div className="navbar-nav mr-auto">
            <li className="nav-item">
              <Link to={"/home"} className="nav-link">
                Home
              </Link>
            </li>

            {showAdminBoard && (
              <li className="nav-item">
                <Link to={"/admin"} className="nav-link">
                  Admin Board
                </Link>
              </li>
            )}

            {currentUser && (
              <li className="nav-item">
                <Link to={"/user"} className="nav-link">
                  User
                </Link>
              </li>
            )}

            

            {showAdminBoard && (
              <li class="nav-item dropdown">
                <a class="nav-link dropdown-toggle" href="#" id="navbarDropdownMenuLink" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                Person Management
                </a>
                <div class="dropdown-menu" aria-labelledby="navbarDropdownMenuLink">
                  <Link to={"/person-management"} className="dropdown-item">
                    List / Search
                  </Link>
                  <Link to={"/person-management/create"} className="dropdown-item">
                    Create
                  </Link>
                </div>
              </li>
            )}



          </div>

          {currentUser ? (
            <div className="navbar-nav ml-auto">
              <li className="nav-item">
                <Link to={"/profile"} className="nav-link">
                  {currentUser.username}
                </Link>
              </li>
              <li className="nav-item">
                <a href="/login" className="nav-link" onClick={this.logOut}>
                  LogOut
                </a>
              </li>
            </div>
          ) : (
            <div className="navbar-nav ml-auto">
              <li className="nav-item">
                <Link to={"/login"} className="nav-link">
                  Login
                </Link>
              </li>

              <li className="nav-item">
                <Link to={"/register"} className="nav-link">
                  Sign Up
                </Link>
              </li>
            </div>
          )}
        </nav>

        <div className="container mt-3">
          <Switch>
            <Route exact path={["/", "/home"]} component={Home} />
            <Route exact path="/login" component={Login} />
            <Route exact path="/register" component={Register} />
            
            <Route exact path="/forgot" component={Forgot} />
            <Route exact path="/forgotUserAccessCode" component={ForgotUserAccessCode} />
            <Route exact path="/resetPassword" component={ResetPassword} />

            <Route exact path="/profile" component={Profile} />
            <Route exact path="/profile/edit" component={ProfileEdit} />

            <Route path="/user" component={BoardUser} />
            <Route path="/admin" component={BoardAdmin} />
            
            <Route exact path={["/person-management"]} component={PersonManagementList} />
            <Route exact path="/person-management/create" component={PersonManagementCreate} />
            <Route path="/person-management/:id" component={PersonManagement} />
          </Switch>
        </div>
      </div>
    );
  }
}

export default App;
