import React, { Component } from "react";

import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import Select from "react-validation/build/select";
import CheckButton from "react-validation/build/button";
import { vrequired, vemail, vdate } from "../common/validators";
import { vfirstName, vlastName, vaddress, vcity, vzip } from "../common/person-validators";

import PersonManagementDataService from "../services/person-management.service";
import PersonService from "../services/person.service";




export default class PersonManagementCreate extends Component {
  constructor(props) {
    super(props);
    this.savePersonManagement = this.savePersonManagement.bind(this);
    //this.newPersonManagement = this.newPersonManagement.bind(this);

    this.onChangeEmail = this.onChangeEmail.bind(this);
    this.onChangeFirstName = this.onChangeFirstName.bind(this);
    this.onChangeLastName = this.onChangeLastName.bind(this);
    this.onChangeAddress = this.onChangeAddress.bind(this);
    this.onChangeGenderId = this.onChangeGenderId.bind(this);
    this.onChangePronounId = this.onChangePronounId.bind(this);
    this.onChangeSexAtBirthId = this.onChangeSexAtBirthId.bind(this);
    this.onChangeStateId = this.onChangeStateId.bind(this);
    this.onChangeBirthday = this.onChangeBirthday.bind(this);
    this.onChangeCity = this.onChangeCity.bind(this);
    this.onChangeZip = this.onChangeZip.bind(this);

    this.state = {
      person_id: "",

      email: "",
      firstName: "",
      lastName: "",
      address: "",
      sex_at_birth_id: "",
      gender_id: "",
      pronoun_id: "",
      state_id: "",
      birthday: "",
      city: "",
      zip: "",

      // dropdown options
      genders: [],
      pronouns: [],
      sexAtBirths: [],
      states: [],

      submitted: false,
      message: "",
    };
  }

  onChangeEmail(e) {
    this.setState({
      email: e.target.value
    });
  }

  onChangeFirstName(e) {
    this.setState({
      firstName: e.target.value
    });
  }

  onChangeLastName(e) {
    this.setState({
      lastName: e.target.value
    });
  }

  onChangeAddress(e) {
    this.setState({
      address: e.target.value
    });
  }

  onChangeGenderId(e) {
    this.setState({
      gender_id: e.target.value
    });
  }

  onChangePronounId(e) {
    this.setState({
      pronoun_id: e.target.value
    });
  }

  onChangeSexAtBirthId(e) {
    this.setState({
      sex_at_birth_id: e.target.value
    });
  }

  onChangeStateId(e) {
    this.setState({
      state_id: e.target.value
    });
  }

  onChangeBirthday(e) {
    this.setState({
      birthday: e.target.value
    });
  }

  onChangeCity(e) {
    this.setState({
      city: e.target.value
    });
  }

  onChangeZip(e) {
    this.setState({
      zip: e.target.value
    });
  }

  savePersonManagement(e) {
    e.preventDefault();

    this.setState({
      message: "",
      loading: true
    });

    this.form.validateAll();

    if (this.checkBtn.context._errors.length === 0) {
      var data = {
        person_id: this.state.person_id,

        email: this.state.email,
        firstName: this.state.firstName,
        lastName: this.state.lastName,
        address: this.state.address,
        sex_at_birth_id: this.state.sex_at_birth_id,
        gender_id: this.state.gender_id,
        pronoun_id: this.state.pronoun_id,
        state_id: this.state.state_id,
        birthday: this.state.birthday,
        city: this.state.city,
        zip: this.state.zip,
      };
      PersonManagementDataService.create(data)
        .then(
          response => {
            this.props.history.push("/person-management");
            window.location.reload();
          },
          error => {
            const resMessage =
              (error.response &&
                error.response.data &&
                error.response.data.message) ||
              error.message ||
              error.toString();

            this.setState({
              successful: false,
              message: resMessage
            });
          }
        )
        .catch(e => {
          console.log(e);
        });
    }
  }

 /* shift + alt + a  
 newPersonManagement() {
    this.setState({
      id: null,
      person_id: "",

      email: "",
      firstName: "",
      lastName: "",
      address: "",
      sex_at_birth_id: "",
      gender_id: "",
      pronoun_id: "",
      state_id: "",
      birthday: "",
      city: "",
      zip: "",
      // dropdown options
      genders: [],
      pronouns: [],
      sexAtBirths: [],
      states: [],

      submitted: false
    });
  } */

  componentDidMount() {
    // get drop down information
    PersonService.getGenders().then(
      response => {
        this.setState({
          genders: response.data.result
        });
      },
      error => {
        this.setState({
          genders:
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString()
        });
      }
    );
    PersonService.getPronouns().then(
      response => {
        this.setState({
