import axios from 'axios';
import authHeader from './auth-header';

const API_URL = 'http://localhost:8000/api/user/';

class UserService {
  getPublicContent() {
    return axios.get(API_URL + 'all');
  }

  getUserBoard() {
    return axios.get(API_URL + 'user', { headers: authHeader() });
  }

  getModeratorBoard() {
    return axios.get(API_URL + 'mod', { headers: authHeader() });
  }

  getAdminBoard() {
    return axios.get(API_URL + 'admin', { headers: authHeader() });
  }



  
  getGenders() {
    return axios.get(API_URL + 'getGenders');
  }
  getPronouns() {
    return axios.get(API_URL + 'getPronouns');
  }
  getSexAtBirths() {
    return axios.get(API_URL + 'getSexAtBirths');
  }
  getStates() {
    return axios.get(API_URL + 'getStates');
  }


  profileEdit(
    username,
    email,
    firstName,
    lastName,
    address,
    birthday,
    gender_id,
    pronoun_id,
    sex_at_birth_id,
    state_id,
    city,
    zip) {
    return axios.post(API_URL + "profileEdit", {
      username,
      email,
      firstName,
      lastName,
      address,
      birthday,
      gender_id,
      pronoun_id,
      sex_at_birth_id,
      state_id,
      city,
      zip
    })
    .then(response => {
      if (response.data.accessToken) {
        localStorage.setItem("user", JSON.stringify(response.data));
      }

      return response.data;
    });
  }
}

export default new UserService();
