import React, { Component } from "react";
import UserManagementDataService from "../services/person-management.service";
import { isEmail, isDate} from "validator";

const required = value => {
  if (!value) {
    return (
      <div className="alert alert-danger" role="alert">
        This field is required!
      </div>
    );
  }
};

const email = value => {
  if (!isEmail(value)) {
    return (
      <div className="alert alert-danger" role="alert">
        This is not a valid email.
      </div>
    );
  }
};

const date = value => {
  if (!isDate(value)) {
      return (
          <div className="alert alert-danger" role="alert">
              This is not a valid date.
          </div>
      );
  }
};

const vusername = value => {
  if (value.length < 3 || value.length > 20) {
    return (
      <div className="alert alert-danger" role="alert">
        The username must be between 3 and 20 characters.
      </div>
    );
  }
};

const vpassword = value => {
  if (value.length < 6 || value.length > 40) {
    return (
      <div className="alert alert-danger" role="alert">
        The password must be between 6 and 40 characters.
      </div>
    );
  }
};

const vfirstName = value => {
  if (value.length < 2 || value.length > 40) {
    return (
      <div className="alert alert-danger" role="alert">
        The first name must be between 2 and 40 characters.
      </div>
    );
  }
};

const vlastName = value => {
  if (value.length < 2 || value.length > 40) {
    return (
      <div className="alert alert-danger" role="alert">
        The last name must be between 2 and 40 characters.
      </div>
    );
  }
};

const vaddress = value => {
  if (value.length < 2 || value.length > 40) {
    return (
      <div className="alert alert-danger" role="alert">
        The address must be between 2 and 40 characters.
      </div>
    );
  }
};

const vcity = value => {
  if (value.length < 2 || value.length > 40) {
    return (
      <div className="alert alert-danger" role="alert">
        The city must be between 2 and 40 characters.
      </div>
    );
  }
};


const vzip = value => {
  const zipletters =  /^([0-9]{5})$/;
  if (!zipletters.test(value) || value.toString().length !== 5) {
    return (
      <div className="alert alert-danger" role="alert">
        The zip must all numbers and 5 characters.
      </div>
    );
  }
};

export default class UserManagementCreate extends Component {
  constructor(props) {
    super(props);
    this.onChangePersonID = this.onChangePersonID.bind(this);
    this.saveUserManagement = this.saveUserManagement.bind(this);
    this.newUserManagement = this.newUserManagement.bind(this);

    this.onChangeEmail = this.onChangeEmail.bind(this);
    this.onChangeFirstName = this.onChangeFirstName.bind(this);
    this.onChangeLastName = this.onChangeLastName.bind(this);
    this.onChangeAddress = this.onChangeAddress.bind(this);
    this.onChangeGenderId = this.onChangeGenderId.bind(this);
    this.onChangePronounId = this.onChangePronounId.bind(this);
    this.onChangeSexAtBirthId = this.onChangeSexAtBirthId.bind(this);
    this.onChangeStateId = this.onChangeStateId.bind(this);
    this.onChangeBirthday = this.onChangeBirthday.bind(this);
    this.onChangeCity = this.onChangeCity.bind(this);
    this.onChangeZip = this.onChangeZip.bind(this);

    this.state = {
      person_id: "",

      email: "",
      firstName: "",
      lastName: "",
      address: "",
      sex_at_birth_id: "",
      gender_id: "",
      pronoun_id: "",
      state_id: "",
      birthday: "",
      city: "",
      zip: "",
      // dropdown options
      genders: [],
      pronouns: [],
      sexAtBirths: [],
      states: [],

      published: false,
      submitted: false,
    };
  }

  onChangePersonID(e) {
    this.setState({
      person_id: e.target.value
    });
  }

  onChangeEmail(e) {
    this.setState({
      email: e.target.value
    });
  }

  onChangeFirstName(e){
    this.setState({
      firstName: e.target.value
    });
  }

  onChangeLastName(e){
    this.setState({
      lastName: e.target.value
    });
  }

  onChangeAddress(e){
    this.setState({
      address: e.target.value
    });
  }

  onChangeGenderId(e){
    this.setState({
      gender_id: e.target.value
    });
  }

  onChangePronounId(e){
    this.setState({
      pronoun_id: e.target.value
    });
  }

  onChangeSexAtBirthId(e){
    this.setState({
      sex_at_birth_id: e.target.value
    });
  }

  onChangeStateId(e){
    this.setState({
      state_id: e.target.value
    });
  }

  onChangeBirthday(e){
    this.setState({
      birthday: e.target.value
    });
  }

  onChangeCity(e){
    this.setState({
      city: e.target.value
    });
  }

  onChangeZip(e){
    this.setState({
      zip: e.target.value
    });
  }

  saveUserManagement() {
    var data = {
      person_id: this.state.person_id,
      description: this.state.description
    };

    UserManagementDataService.create(data)
      .then(response => {
        this.setState({
          id: response.data.id,
          person_id: response.data.person_id,
          description: response.data.description,
          published: response.data.published,

          submitted: true
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  newUserManagement() {
    this.setState({
      id: null,
      person_id: "",
      description: "",
      published: false,

      submitted: false
    });
  }

  render() {
    return (
      <div className="submit-form">
        {this.state.submitted ? (
          <div>
            <h4>You submitted successfully!</h4>
            <button className="btn btn-success" onClick={this.newUserManagement}>
              Add
            </button>
          </div>
        ) : (
          <div>
            <div className="form-group">
              <label htmlFor="person_id">Person ID</label>
              <input
                type="text"
                className="form-control"
                id="person_id"
                required
                value={this.state.person_id}
                onChange={this.onChangePersonID}
                name="person_id"
              />
            </div>


            <div className="form-group">
              <label htmlFor="email">Email</label>
              <Input
                type="text"
                className="form-control"
                name="email"
                value={this.state.email}
                onChange={this.onChangeEmail}
                validations={[required, email]}
              />
            </div>

            <div className="form-group">
              <label htmlFor="password">Password</label>
              <Input
                type="password"
                className="form-control"
                name="password"
                value={this.state.password}
                onChange={this.onChangePassword}
                validations={[required, vpassword]}
              />
            </div>

            <div className="form-group">
              <label htmlFor="firstName">FirstName</label>
              <Input
                type="text"
                className="form-control"
                name="firstName"
                value={this.state.firstName}
                onChange={this.onChangeFirstName}
                validations={[required, vfirstName]}
              />
            </div>

            <div className="form-group">
              <label htmlFor="lastName">LastName</label>
              <Input
                type="text"
                className="form-control"
                name="lastName"
                value={this.state.lastName}
                onChange={this.onChangeLastName}
                validations={[required, vlastName]}
              />
            </div>

            <div className="form-group">
              <label htmlFor="address">Address</label>
              <Input
                type="text"
                className="form-control"
                name="address"
                value={this.state.address}
                onChange={this.onChangeAddress}
                validations={[required, vaddress]}
              />
            </div>

            <div className="form-group">
              <label htmlFor="birthday">Birthday</label>
              <Input
                type="text"
                className="form-control"
                name="birthday"
                value={this.state.birthday}
                onChange={this.onChangeBirthday}
                validations={[required, date]}
              />
            </div>

            <div className="form-group">
              <label htmlFor="gender_id">Gender</label>
              <Select
                onChange={this.onChangeGenderId}
                validations={[required]}
              >
                <option value="">Select One</option>
                {this.state.genders.length > 0 &&
                  this.state.genders.map(item => (
                    <option key={item.gender_id} value={item.gender_id}>
                      {item.gender}
                    </option>
                  ))}
              </Select>
            </div>

            <div className="form-group">
              <label htmlFor="pronoun_id">Pronoun</label>
              <Select
                onChange={this.onChangePronounId}
                validations={[required]}
              >
                <option value="">Select One</option>
                {this.state.pronouns.length > 0 &&
                  this.state.pronouns.map(item => (
                    <option key={item.pronoun_id} value={item.pronoun_id}>
                      {item.pronoun}
                    </option>
                  ))}
              </Select>
            </div>

            <div className="form-group">
              <label htmlFor="sex_at_birth_id">Sex At Birth</label>
              <Select
                onChange={this.onChangeSexAtBirthId}
                validations={[required]}
              >
                <option value="">Select One</option>
                {this.state.sexAtBirths.length > 0 &&
                  this.state.sexAtBirths.map(item => (
                    <option key={item.sex_at_birth_id} value={item.sex_at_birth_id}>
                      {item.sex_at_birth}
                    </option>
                  ))}
              </Select>
            </div>

            <div className="form-group">
              <label htmlFor="state_id">State</label>
              <Select
                onChange={this.onChangeStateId}
                validations={[required]}
              >
                <option value="">Select One</option>
                {this.state.states.length > 0 &&
                  this.state.states.map(item => (
                    <option key={item.state_id} value={item.state_id}>
                      {item.state}
                    </option>
                  ))}
              </Select>
            </div>

            <div className="form-group">
              <label htmlFor="city">City</label>
              <Input
                type="text"
                className="form-control"
                name="city"
                value={this.state.city}
                onChange={this.onChangeCity}
                validations={[required, vcity]}
              />
            </div>

            <div className="form-group">
              <label htmlFor="zip">Zip</label>
              <Input
                type="text"
                className="form-control"
                name="zip"
                value={this.state.zip}
                onChange={this.onChangeZip}
                validations={[required, vzip]}
              />
            </div>

            <button onClick={this.saveUserManagement} className="btn btn-success">
              Submit
            </button>
          </div>
        )}
      </div>
    );
  }
}
