import React, { Component } from "react";
import PersonManagementDataService from "../services/person-management.service";
import { Link } from "react-router-dom";

export default class PersonManagementList extends Component {
  constructor(props) {
    super(props);
    this.onChangeSearchPersonID = this.onChangeSearchPersonID.bind(this);
    this.retrievePersonManagement = this.retrievePersonManagement.bind(this);
    this.refreshList = this.refreshList.bind(this);
    this.setActivePersonManagement = this.setActivePersonManagement.bind(this);
    this.removeAllPersonManagement = this.removeAllPersonManagement.bind(this);
    this.searchPersonID = this.searchPersonID.bind(this);

    this.state = {
      personManagement: [],
      currentPersonManagement: null,
      currentIndex: -1,
      searchPersonID: ""
    };
  }

  componentDidMount() {
    this.retrievePersonManagement();
  }

  onChangeSearchPersonID(e) {
    const searchPersonID = e.target.value;

    this.setState({
      searchPersonID: searchPersonID
    });
  }

  retrievePersonManagement() {
    PersonManagementDataService.getAll()
      .then(response => {
        this.setState({
          personManagement: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  refreshList() {
    this.retrievePersonManagement();
    this.setState({
      currentPersonManagement: null,
      currentIndex: -1
    });
  }

  setActivePersonManagement(personManagement, index) {
    this.setState({
      currentPersonManagement: personManagement,
      currentIndex: index
    });
  }

  removeAllPersonManagement() {
    PersonManagementDataService.deleteAll()
      .then(response => {
        console.log(response.data);
        this.refreshList();
      })
      .catch(e => {
        console.log(e);
      });
  }

  searchPersonID() {
    this.setState({
      currentPersonManagement: null,
      currentIndex: -1
    });

    PersonManagementDataService.findByPersonID(this.state.searchPersonID)
      .then(response => {
        this.setState({
          personManagement: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const { searchPersonID, personManagement, currentPersonManagement, currentIndex } = this.state;

    return (
      <div className="list row">
        <div className="col-md-8">
          <div className="input-group mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Search by person_id"
              value={searchPersonID}
              onChange={this.onChangeSearchPersonID}
            />
            <div className="input-group-append">
              <button
                className="btn btn-outline-secondary"
                type="button"
                onClick={this.searchPersonID}
              >
                Search
              </button>
            </div>
          </div>
        </div>
        <div className="col-md-6">
          <h4>Person Management List</h4>

          <ul className="list-group">
            {personManagement &&
              personManagement.map((personManagement, index) => (
                <li
                  className={
                    "list-group-item " +
                    (index === currentIndex ? "active" : "")
                  }
                  onClick={() => this.setActivePersonManagement(personManagement, index)}
                  key={index}
                >
                  {personManagement.person_id}
                </li>
              ))}
          </ul>
        </div>
        <div className="col-md-6">
          {currentPersonManagement ? (
            <div>
              <h4>PersonManagement</h4>
              <div>
                <label>
                  <strong>Name:</strong>
                </label>{" "}
                {currentPersonManagement.first_name} {currentPersonManagement.last_name}
              </div>
              <div>
                <label>
                  <strong>Name:</strong>
                </label>{" "}
                {currentPersonManagement.first_name} {currentPersonManagement.last_name}
              </div>
              <div>
                <label>
                  <strong>Birthday:</strong>
                </label>{" "}
                {currentPersonManagement.birthday}
              </div>
              <div>
                <label>
                  <strong>Address:</strong>
                </label>{" "}
                {currentPersonManagement.address}
              </div>
              <div>
                <label>
                  <strong>City:</strong>
                </label>{" "}
                {currentPersonManagement.city}
              </div>
              <div>
                <label>
                  <strong>Zip:</strong>
                </label>{" "}
                {currentPersonManagement.zip}
              </div>
              <div>
                <label>
                  <strong>Description:</strong>
                </label>{" "}
                {currentPersonManagement.description}
              </div>
              <div>
                <label>
                  <strong>Status:</strong>
                </label>{" "}
                {currentPersonManagement.published ? "Published" : "Pending"}
              </div>

              <Link
                to={"/person-management/" + currentPersonManagement.id}
                className="btn btn-primary btn-block"
              >
                Edit
              </Link>
            </div>
          ) : (
            <div>
              <br />
              <p>Please click on a User...</p>
            </div>
          )}
        </div>
      </div>
    );
  }
}
