import React, { Component } from "react";
import UserManagementDataService from "../services/person-management.service";

export default class UserManagement extends Component {
  constructor(props) {
    super(props);
    this.onChangePersonID = this.onChangePersonID.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.getUserManagement = this.getUserManagement.bind(this);
    this.updateUserManagement = this.updateUserManagement.bind(this);
    this.deleteUserManagement = this.deleteUserManagement.bind(this);

    this.state = {
      currentUserManagement: {
        person_id: "",
        description: "",
        published: false
      },
      message: ""
    };
  }

  componentDidMount() {
    this.getUserManagement(this.props.match.params.person_id);
  }

  onChangePersonID(e) {
    const person_id = e.target.value;

    this.setState(function(prevState) {
      return {
        currentUserManagement: {
          ...prevState.currentUserManagement,
          person_id: person_id
        }
      };
    });
  }

  onChangeDescription(e) {
    const description = e.target.value;

    this.setState(prevState => ({
      currentUserManagement: {
        ...prevState.currentUserManagement,
        description: description
      }
    }));
  }

  getUserManagement(person_id) {
    UserManagementDataService.get(person_id)
      .then(response => {
        this.setState({
          currentUserManagement: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  updateUserManagement() {
    UserManagementDataService.update(
      this.state.currentUserManagement.person_id,
      this.state.currentUserManagement
    )
      .then(response => {
        console.log(response.data);
        this.setState({
          message: "The userManagement was updated successfully!"
        });
      })
      .catch(e => {
        console.log(e);
      });
  }

  deleteUserManagement() {    
    UserManagementDataService.delete(this.state.currentUserManagement.person_id)
      .then(response => {
        console.log(response.data);
        this.props.history.push('/person-management')
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const { currentUserManagement } = this.state;

    return (
      <div>
        {currentUserManagement ? (
          <div className="edit-form">
            <h4>Edit User</h4>
            <form>
              <div className="form-group">
                <label htmlFor="person_id">PersonID</label>
                <input
                  type="text"
                  className="form-control"
                  id="person_id"
                  value={currentUserManagement.person_id}
                  onChange={this.onChangePersonID}
                />
              </div>
              <div className="form-group">
                <label htmlFor="description">Description</label>
                <input
                  type="text"
                  className="form-control"
                  id="description"
                  value={currentUserManagement.description}
                  onChange={this.onChangeDescription}
                />
              </div>

              <div className="form-group">
                <label>
                  <strong>Status:</strong>
                </label>
                {currentUserManagement.published ? "Published" : "Pending"}
              </div>
            </form>

            {currentUserManagement.published ? (
              <button
                className="btn btn-primary btn-block"
                onClick={() => this.updatePublished(false)}
              >
                UnPublish
              </button>
            ) : (
              <button
                className="btn btn-primary btn-block"
                onClick={() => this.updatePublished(true)}
              >
                Publish
              </button>
            )}

            <button
              className="btn btn-primary btn-block"
              onClick={this.deleteUserManagement}
            >
              Delete
            </button>

            <button
              type="submit"
              className="btn btn-primary btn-block"
              onClick={this.updateUserManagement}
            >
              Update
            </button>
            <p>{this.state.message}</p>
          </div>
        ) : (
          <div>
            <br />
            <p>Please click on a User...</p>
          </div>
        )}
      </div>
    );
  }
}
