import axios from 'axios';
import authHeader from './auth-header';

const API_URL = 'http://localhost:8000/api/user/';

class UserService {
  getPublicContent() {
    return axios.get(API_URL + 'all');
  }

  getUserBoard() {
    return axios.get(API_URL + 'user', { headers: authHeader() });
  }

  getModeratorBoard() {
    return axios.get(API_URL + 'mod', { headers: authHeader() });
  }

  getAdminBoard() {
    return axios.get(API_URL + 'admin', { headers: authHeader() });
  }



  
  getGenders() {
    return axios.get(API_URL + 'getGenders');
  }
  getPronouns() {
    return axios.get(API_URL + 'getPronouns');
  }
  getSexAtBirths() {
    return axios.get(API_URL + 'getSexAtBirths');
  }
  getStates() {
    return axios.get(API_URL + 'getStates');
  }


  

  profileEdit(data) {
    return http.put('/profile/edit/', data, {method: 'put'})
      .then(response => {
        // combine old and new user data
        var user = JSON.parse(localStorage.getItem("user"));
        user = {...user, ...response.data};
        localStorage.setItem("user", JSON.stringify(user));

        return response.data;
      })
      .catch(error => {
        console.log(error);
      });
  }
}

export default new UserService();
