import React, { Component } from "react";
import UserManagementDataService from "../services/person-management.service";

export default class UserManagementCreate extends Component {
  constructor(props) {
    super(props);
    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.saveUserManagement = this.saveUserManagement.bind(this);
    this.newUserManagement = this.newUserManagement.bind(this);

    this.state = {
      person_id: "",
      description: "", 
      published: false,
      submitted: false
    };
  }

  onChangeUsername(e) {
    this.setState({
      person_id: e.target.value
    });
  }

  onChangeDescription(e) {
    this.setState({
      description: e.target.value
    });
  }

  saveUserManagement() {
    var data = {
      person_id: this.state.person_id,
      description: this.state.description
    };

    UserManagementDataService.create(data)
      .then(response => {
        this.setState({
          id: response.data.id,
          person_id: response.data.person_id,
          description: response.data.description,
          published: response.data.published,

          submitted: true
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  newUserManagement() {
    this.setState({
      id: null,
      person_id: "",
      description: "",
      published: false,

      submitted: false
    });
  }

  render() {
    return (
      <div className="submit-form">
        {this.state.submitted ? (
          <div>
            <h4>You submitted successfully!</h4>
            <button className="btn btn-success" onClick={this.newUserManagement}>
              Add
            </button>
          </div>
        ) : (
          <div>
            <div className="form-group">
              <label htmlFor="person_id">Username</label>
              <input
                type="text"
                className="form-control"
                id="person_id"
                required
                value={this.state.person_id}
                onChange={this.onChangeUsername}
                name="person_id"
              />
            </div>

            <div className="form-group">
              <label htmlFor="description">Description</label>
              <input
                type="text"
                className="form-control"
                id="description"
                required
                value={this.state.description}
                onChange={this.onChangeDescription}
                name="description"
              />
            </div>

            <button onClick={this.saveUserManagement} className="btn btn-success">
              Submit
            </button>
          </div>
        )}
      </div>
    );
  }
}
