import React, { Component } from "react";
import UserManagementDataService from "../services/user-management.service";

export default class UserManagement extends Component {
  constructor(props) {
    super(props);
    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.getUserManagement = this.getUserManagement.bind(this);
    this.updatePublished = this.updatePublished.bind(this);
    this.updateUserManagement = this.updateUserManagement.bind(this);
    this.deleteUserManagement = this.deleteUserManagement.bind(this);

    this.state = {
      currentUserManagement: {
        id: null,
        username: "",
        description: "",
        published: false
      },
      message: ""
    };
  }

  componentDidMount() {
    this.getUserManagement(this.props.match.params.id);
  }

  onChangeUsername(e) {
    const username = e.target.value;

    this.setState(function(prevState) {
      return {
        currentUserManagement: {
          ...prevState.currentUserManagement,
          username: username
        }
      };
    });
  }

  onChangeDescription(e) {
    const description = e.target.value;

    this.setState(prevState => ({
      currentUserManagement: {
        ...prevState.currentUserManagement,
        description: description
      }
    }));
  }

  getUserManagement(username) {
    UserManagementDataService.get(username)
      .then(response => {
        this.setState({
          currentUserManagement: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  updateUserManagement() {
    UserManagementDataService.update(
      this.state.currentUserManagement.username,
      this.state.currentUserManagement
    )
      .then(response => {
        console.log(response.data);
        this.setState({
          message: "The userManagement was updated successfully!"
        });
      })
      .catch(e => {
        console.log(e);
      });
  }

  deleteUserManagement() {    
    UserManagementDataService.delete(this.state.currentUserManagement.id)
      .then(response => {
        console.log(response.data);
        this.props.history.push('/user-management')
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const { currentUserManagement } = this.state;

    return (
      <div>
        {currentUserManagement ? (
          <div className="edit-form">
            <h4>Edit User</h4>
            <form>
              <div className="form-group">
                <label htmlFor="username">Username</label>
                <input
                  type="text"
                  className="form-control"
                  id="username"
                  value={currentUserManagement.username}
                  onChange={this.onChangeUsername}
                />
              </div>
              <div className="form-group">
                <label htmlFor="description">Description</label>
                <input
                  type="text"
                  className="form-control"
                  id="description"
                  value={currentUserManagement.description}
                  onChange={this.onChangeDescription}
                />
              </div>

              <div className="form-group">
                <label>
                  <strong>Status:</strong>
                </label>
                {currentUserManagement.published ? "Published" : "Pending"}
              </div>
            </form>

            {currentUserManagement.published ? (
              <button
                className="btn btn-primary btn-block"
                onClick={() => this.updatePublished(false)}
              >
                UnPublish
              </button>
            ) : (
              <button
                className="btn btn-primary btn-block"
                onClick={() => this.updatePublished(true)}
              >
                Publish
              </button>
            )}

            <button
              className="btn btn-primary btn-block"
              onClick={this.deleteUserManagement}
            >
              Delete
            </button>

            <button
              type="submit"
              className="btn btn-primary btn-block"
              onClick={this.updateUserManagement}
            >
              Update
            </button>
            <p>{this.state.message}</p>
          </div>
        ) : (
          <div>
            <br />
            <p>Please click on a User...</p>
          </div>
        )}
      </div>
    );
  }
}
