import http from "../http-common";
import authHeader from './auth-header';
class UserService {
  getPublicContent() {
    return http.get('user/all');
  }

  getUserBoard() {
    return http.get('user/user', { headers: authHeader() });
  }

  getModeratorBoard() {
    return http.get('user/mod', { headers: authHeader() });
  }

  getAdminBoard() {
    return http.get('user/admin', { headers: authHeader() });
  }



  
  getGenders() {
    return http.get('user/getGenders');
  }
  getPronouns() {
    return http.get('user/getPronouns');
  }
  getSexAtBirths() {
    return http.get('user/getSexAtBirths');
  }
  getStates() {
    return http.get('user/getStates');
  }


  

  profileEdit(data) {
    return http.put('/profile/edit/', data, {method: 'put'})
      .then(response => {
        // combine old and new user data
        var user = JSON.parse(localStorage.getItem("user"));
        user = {...user, ...response.data};
        localStorage.setItem("user", JSON.stringify(user));

        return response.data;
      })
      .catch(error => {
        console.log(error);
      });
  }
}

export default new UserService();
